targets.expr:
(
  node_memory_MemTotal_bytes{job="$job", instance="$instance"}
-
  node_memory_MemFree_bytes{job="$job", instance="$instance"}
-
  node_memory_Buffers_bytes{job="$job", instance="$instance"}
-
  node_memory_Cached_bytes{job="$job", instance="$instance"}
)

sum(
  max by (device) (
    node_filesystem_size_bytes{job="$job", instance="$instance", fstype!=""}
  -
    node_filesystem_avail_bytes{job="$job", instance="$instance", fstype!=""}
  )
)

sum(
  max by (device) (
    node_filesystem_avail_bytes{job="node", instance="$instance", fstype!=""}
  )
)
